import React,{useState, useRef, useEffect} from "react";

function ProfilePic({changeProfile}) {
    const hiddenFileInput = useRef(null);


    
    const handleClick = () => {
        hiddenFileInput.current.click();
    };
  return (
    <div className="profilePic darkBg">
      <div className="changePic centered">
        <div>
          <h2>Change Profile Photo</h2>
        </div>
        <div style={{borderTop: "1px solid #00000030"}}>
          <button className="upload-btn" style={{color: "#1EA1f7", }} onClick={handleClick}>Upload Photo</button>
          <input type="file" ref={hiddenFileInput} accept="image/*" style={{ display: "none" }} />
        </div>
        <div style={{borderTop: "1px solid #00000030"}}>
            <button className="upload-btn" style={{color:"#ed4956"}}>Remove Current Photo</button>
        </div>
        <div style={{borderTop: "1px solid #00000030"}} onClick={changeProfile}>
            <button style={{background: "none", border:"none", cursor:"pointer",fontSize:"15px"}}>Cancel</button>
        </div>
      </div>
    </div>
  );
}

export default ProfilePic;
