import React,{useEffect,useState} from 'react'
import './Profile.css'
import PostDetail from './PostDetail'

function Profile() {
  const [pic, setPic] = useState([])
  const [show, setShow] = useState(false)
  const [posts, setPosts] = useState([])

  const toggleDetails = (posts) =>

  useEffect(() => {
    fetch("http://localhost:5000/myposts", {
      headers:{
       Authorization : "Bearer " + localStorage.getItem("jwt")
      }
    })
    .then(res => res.json())
    .then((result) => {
      setPic(result)
      console.log(pic) 
    })
  }, [])
  

  return (
    <div className='profile'>
      {/* Profile frame */}
      <div className="profile-frame">
        {/* Profile-pic */}
        <div className="profile-pic">
          <img src="https://images.unsplash.com/photo-1522075469751-3a6694fb2f61?q=80&w=1780&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
        </div>
        {/* Profile-data */}
        <div className="profile-data">
          <h1>{JSON.parse(localStorage.getItem("user")).name}</h1>
          <div className="profile-info" style={{display: "flex"}}>
            <p>40 posts</p>
            <p>40 Followers</p>
            <p>40 Following</p>
          </div>
        </div>
      </div>
      <hr style={{
        width: "90%", 
        opacity:"0.8",
        margin: "25px auto"
      }}/>
      {/* Gallery */}
      <div className="gallery">
        {pic.map((pic) => {
          return <img key={pic._id} src={pic.photo} alt=''></img>
        })}
      </div>
      <PostDetail />
    </div>
  );
}

export default Profile
