import React,{useEffect,useState} from 'react'
import './Profile.css'
import PostDetail from './PostDetail'
import { useParams } from 'react-router-dom'

function UserProfile() {

const {userid} = useParams()
  const [pic, setPic] = useState([])
  console.log(userid)
  const [user, setUser] = useState("")
  const [posts, setPosts] = useState([])
  const [isFollow, setisFollow] = useState(false)

  // to follow user
  const followUser = (userId) => {
    fetch("http://localhost:5000/follow",{
      method:"put",
      headers:{
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body:JSON.stringify({
        followId:userId,
      }),
    })
    .then((res) => res.json())
    .then((data) => {
      console.log(data)
      setisFollow(true)
    })
  }

  // to unfollow user
  const unfollowUser = (userId) => {
    fetch("http://localhost:5000/unfollow",{
      method:"put",
      headers:{
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body:JSON.stringify({
        followId:userId
      })
    })
    .then((res) => res.json(res))
    .then((data) => {
      console.log(data)
      setisFollow(false)
    })
  }


  useEffect(() => {
    fetch(`http://localhost:5000/user/${userid}`, {
      headers:{
       Authorization : "Bearer " + localStorage.getItem("jwt")
      }
    })
    .then(res => res.json())
    .then((result) => {
        console.log(result)
      setUser(result.user)
      setPosts(result.post)
      if(result.user.followers.includes(JSON.parse(localStorage.getItem("user"))._id)){
        setisFollow(true)
      }
    })
  }, [isFollow])
  

  return (
    <div className='profile'>
      {/* Profile frame */}
      <div className="profile-frame">
        {/* Profile-pic */}
        <div className="profile-pic">
          <img src="https://images.unsplash.com/photo-1522075469751-3a6694fb2f61?q=80&w=1780&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
        </div>
        {/* Profile-data */}
        <div className="profile-data">
          <div style={{display:"flex", alignItems:"center", justifyContent:"space-between"}}>
            <h1>{user.name}</h1>
            <button className='followBtn' onClick={() => {
              if(isFollow){
                unfollowUser(user._id)
              }
              else{
                followUser(user._id)
              }
            }}>{isFollow ? "Unfollow" : "Follow"}</button>
          </div>
          <div className="profile-info" style={{display: "flex"}}>
            <p>{posts.length} Posts</p>
            <p>{posts.followers.length} Followers</p>
            <p>{po} Following</p>
          </div>
        </div>
      </div>
      <hr style={{
        width: "90%", 
        opacity:"0.8",
        margin: "25px auto"
      }}/>
      {/* Gallery */}
      <div className="gallery">
        {pic.map((pic) => {
          return <img key={pic._id} src={pic.photo} alt='' onClick={() =>{
            // toggleDetails(pic)
          }} className='item'></img>
        })}
      </div>
      {/* {show && 
        <PostDetail item={posts} toggleDetails = {toggleDetails}/>
      } */}
    </div>
  );
}

export default UserProfile
