import React, { useState, useRef, useEffect } from "react";

function ProfilePic({ changeProfile }) {
  const hiddenFileInput = useRef(null);
  const [image, setImage] = useState("second");
  const [url, setUrl] = useState("");

  // posting image to cloudinary

  const postDetails = () => {
    const data = new FormData();
    data.append("file", image);
    data.append("upload_preset", "insta-clone");
    data.append("cloud_name", "spacious");
    fetch("https://api.cloudinary.com/v1_1/spacious/image/upload", {
      method: "post",
      body: data,
    })
      .then((res) => res.json())
      .then((data) => setUrl(data.url))
      .catch((err) => console.log(err));
  };

  const handleClick = () => {
    hiddenFileInput.current.click();
  };
  
  return (
    <div className="profilePic darkBg">
      <div className="changePic centered">
        <div>
          <h2>Change Profile Photo</h2>
        </div>
        <div style={{ borderTop: "1px solid #00000030" }}>
          <button
            className="upload-btn"
            style={{ color: "#1EA1f7" }}
            onClick={handleClick}
          >
            Upload Photo
          </button>
          <input
            type="file"
            ref={hiddenFileInput}
            accept="image/*"
            style={{ display: "none" }}
            onChange={(e) => {
              setImage(e.target.files[0]);
            }}
          />
        </div>
        <div style={{ borderTop: "1px solid #00000030" }}>
          <button className="upload-btn" style={{ color: "#ed4956" }}>
            Remove Current Photo
          </button>
        </div>
        <div
          style={{ borderTop: "1px solid #00000030" }}
          onClick={changeProfile}
        >
          <button
            style={{
              background: "none",
              border: "none",
              cursor: "pointer",
              fontSize: "15px",
            }}
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
}

export default ProfilePic;
