import { React,useState } from "react";
import "./SignIn.css";
import logo from "../img/logo.png";
import { Link,useNavigate } from "react-router-dom";
import { toast } from 'react-toastify';

function SignIn() {

  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const navigate = useNavigate();

  const notifyA = (msg) => toast.error(msg)
  const notifyB = (msg) => toast.success(msg)

  const emailRegex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/

  const postData = () => {

    // email validation
    if(!emailRegex.test(email)){
      notifyA("Invalid Email");
      return
    }
    else if(!passRegex.test(password)){
      notifyA("Please choose a Strong Password")
      return
    }

    // Sending data to server
    fetch("http://localhost:5000/signup", {
      method: "post",
      headers:{
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        email : email,
        password : password
      })
    }).then(res => res.json())                      // convert to json
    .then(data => {
      if(data.error){
        notifyA(data.error);
      }
      else{
        notifyB(data.message);
        navigate("/")
      }
      console.log(data)                           // show in console
    })             

  }

  return (
    <div className="signIn"> 
    <div style={{width:"25%"}}>               
      <div className="loginForm">
        <img className="signInlogo" src={logo} alt="" />
        <div>
          <input type="email" name="email" value={email} id="email" placeholder="Email" onChange={(e) => {setEmail(e.target.value)}}/>
        </div>
        <div>
          <input
            type="password"
            name="password"
            id="password"
            value={password}
            placeholder="Enter Password"
            onChange={(e) => {setPassword(e.target.value)}}
          />
        </div>
        <input type="submit" id="login-btn" value="Sign In" />
      </div>
      <div className="loginForm2">
        Don't have an account?
        <Link to="/signup">
          <span style={{ color: "blue", cursor: "pointer" }}>Sign Up</span>
        </Link>
      </div>
      </div>
    </div>
  );
}

export default SignIn;
