import React from 'react'
import './CreatePost.css'

function CreatePost() {

    const loadfile = (event) => {
        var output = document.getElementById('output');
        output.src = URL.createObjectURL(event.target.files[0]);
        output.onload = function() {
            URL.revokeObjectURL(output.src) // free memory
        }
    }

  return (
    <div className='createPost'>
        {/* Header */}
        <div className="post-header">
            <h4 style={{margin: "3px auto"}}>Create New Post</h4>
            <button id='post-btn'>Share</button>
        </div>
        {/* Image Preview */}
        <div className="main-div">
            <img src="" id alt="" />
            <input type="file" accept='image/*'/>
        </div>
        {/* Details */}
        <div className="details">
            <div className="card-header">
                <div className="card-pic">
                    <img src="https://images.unsplash.com/photo-1522075469751-3a6694fb2f61?q=80&w=1780&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
                </div>
                <h5>Ramesh</h5>
            </div>
            <textarea type="text"placeholder='Write a caption....'></textarea>
        </div>
    </div>
  )
}

export default CreatePost
